export default {
    data() {
        return {
            //...
            debounceTime: null, //
            //...
        }
    },
    watch: {
      keywords: {
        handler(val) {
          clearTimeout(this.debounceTime)
          this.debounceTime = setTimeout(() => {
            if (val) {
              axios.get(`https://restapi.amap.com/v3/place/text?key=${AMAP_AK}&keywords=${val}`).then(response => {
                this.posList = response.data.pois;
              })
            }
            else {
              this.posList = [];
            }
          },800)
        }
      }
    },
}
